import { Text, View, TextInput, TouchableOpacity } from "react-native";
import { useForm } from "react-hook-form";
import { styles } from "../../style/style";
import { Users } from "../../models/users";
import { useEffect, useState } from "react";
import { NavigationService } from "../../service/NavigationService";
import { Sale } from "../../models/sale";
import { SaleService } from "../../service/SaleService";
import { Products } from "../../models/products";
import { SelectList } from "react-native-dropdown-select-list";
import { UsersService } from "../../service/UsersService";
import { ProductsService } from "../../service/ProductsService";

// localstorage
const PRODUCT_INFO = "@productInfo";
const SALE_INFO = "@saleInfo";

export default function SaleFrom({ navigation, route }) {
  //#region atributos
  // const [sales, setSales] = useState([]);
  // const [sale, setSale] = useState(new Sale());
  let sale = new Sale();
  let sales = [];
  let users = [];
  let products = [];

  // const [products, setProducts] = useState([]);
  // const [users, setUsers] = useState([]);

  const [selected, setSelected] = useState([]);

  const navigationService = new NavigationService();
  const saleService = new SaleService();
  const userService = new UsersService();
  const productsService = new ProductsService();

  const [formData, setFormData] = useState(sale);
  const [errorMess, setErrorMess] = useState("");

  const { handleSubmit, reset } = useForm({
    defaultValues: new Users(),
  });
  //#endregion

  //#region functions

  //#region services
  const getUsers = async () => {
    await userService
      .getAllUsers()
      .then((response) => {
        if (response.data) {
          console.log(response.data);
          // setUsers(response.data);
          users = response.data;
        }
      })
      .catch((e) => console.log(e));
  };
  const getProducts = async () => {
    await productsService
      .getAllProducts()
      .then((response) => {
        if (response.data) {
          console.log(response.data);
          products = response.data;
        }
      })
      .catch((e) => console.log(e));
  };
  //create user
  const createSale = async () => {
    await saleService
      .createSale(formData)
      .then((response) => {
        if (response) {
          setErrorMess("Venta agregada con exito.");
          setTimeout(() => {
            setErrorMess("");
            navigationService.logout({ navigation });
          }, 2000);
        }
      })
      .catch((e) => console.log(e));
  };

  const updateSale = async () => {
    await saleService
      .updateSale(sale._id, formData)
      .then((response) => {
        if (response) {
          setErrorMess("Venta actualizada con exito.");
          setTimeout(() => {
            setErrorMess("");
            navigationService.navigateUsersList({ navigation });
          }, 2000);
        }
      })
      .catch((e) => console.log(e));
  };
  //#endregion

  //#region events
  const onChange = (e, type) => {
    setFormData({ ...formData, [type]: e });
  };

  const userSelect = async (selectUser) => {
    if (selectUser) {
      const findUser = users.find((x) => x.username === selectUser.username);
      if (findUser) {
        sale.username = findUser.username;
      }
    }
  };

  const productSelect = async (selectProduct) => {
    if (selectProduct) {
      const findProduct = products.find((x) => x.name === selectProduct.name);
      if (findProduct) {
        sale.name = findProduct.name;
        sale.price = findProduct.price;
      }
    }
  };

  useEffect(() => {
    getUsers();
    getProducts();
  }, []);
  //#endregion

  //#endregion
  return (
    <View style={styles.container}>
      <Text style={styles.title}>{sale._id ? `Update` : "Add"}</Text>
      <Text style={styles.subtitle}>
        {sale._id ? `` : "Ingresa un producto"}
      </Text>
      <SelectList
        setSelected={(val) => setSelected(val)}
        data={users}
        save="username"
        onSelect={() => alert(selected)}
        label="User"
      />
      <SelectList
        setSelected={(val) => setSelected(val)}
        data={products}
        save="name"
        onSelect={() => alert(sale)}
        label="Product"
      />
      {/* <TextInput
        style={styles.textInput}
        placeholder="Name"
        onChangeText={(e) => onChange(e, "name")}
        defaultValue={sale.username ? sale.username : formData.username}
      />
      <TextInput
        style={styles.textInput}
        placeholder="Product"
        onChangeText={(e) => onChange(e, "name")}
        defaultValue={sale.product ? sale.product : formData.product}
      /> */}
      <TextInput
        style={styles.textInput}
        placeholder="Price"
        onChangeText={(e) => onChange(e, "price")}
        defaultValue={sale.price ? sale.price : formData.price}
      />
      <TouchableOpacity
        style={styles.button}
        onPress={() => {
          if (formData.name !== "" && formData.price !== "") {
            product._id ? updateProduct() : createProduct();
          } else {
            setErrorMess("Todos los campos son obligatorios.");
            setTimeout(() => {
              setErrorMess("");
            }, 2000);
          }
        }}
      >
        <Text> {sale._id ? `Update` : "Add"}</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          sale._id
            ? navigationService.navigateMenu({ navigation })
            : navigationService.navigateProductsList({ navigation });
        }}
      >
        <Text style={styles.text}>
          {sale._id ? "Volver al menú" : `Seleccionar productos`}
        </Text>
      </TouchableOpacity>

      <Text style={{ fontWeight: "bold", marginTop: 10, color: "black" }}>
        {errorMess}
      </Text>
    </View>
  );
}
